import React, { useEffect, useState } from "react";
import Input from "../../Input/Input";
import classes from "./Crypto.module.css";
import {
  projectListCrypto,
  projectListCryptoUl,
} from "../../ReusableCryptoArray";
import axios from "axios";
import { type } from "@testing-library/user-event/dist/type";

function Crypto(props) {
  const {
    loanValueBorrow,
    loanWInterestBorrow,
    loanValueDollars,
    repayInterestPlusLoan,
  } = { ...props.data };

  const [price, setPrice] = useState();
  const [purchasePrice, setPurchasePrice] = useState();
  const [purchasePriceType, setPurchasePriceType] = useState('text');
  const [amountPurchased, setAmountPurchased] = useState();
  const [amountPurchasedType, setAmountPurchasedType] = useState('text');
  const [totalInvested, setTotalInvested] = useState();
  const [salePrice, setSalePrice] = useState();
  const [salePriceType, setSalePriceType] = useState('text');
  const [profitLoss, setProfitLoss] = useState();
  const [profitLossBorrow, setProfitLossBorrow] = useState();
  const [finalPosition, setFinalPosition] = useState();
  const [maxAmount, setMaxAmount] = useState();

  const ChangeProjectCrypto = (e) => {
    let projectId;
    let selectedToken;
    if (e.length > 1) {
      projectId = e;
      selectedToken = null;
    } else {
      selectedToken = e.target.value;
      projectId = null;
    }

    function sortPolicy() {
      if (projectId !== null) {
        return projectId;
      } else {
        return selectedToken;
      }
    }
    const config = { headers: { Accept: "application/json" } };
    const params = {
      coinId: sortPolicy(),
    };
    axios
      .get(`https://api.coingecko.com/api/v3/coins/${params.coinId}`, config)
      .then((res) => {
        console.log(res);
        setTimeout(() => {
          setPrice(res.data.market_data.current_price.usd.toLocaleString());
        }, 500);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    const aave = document.querySelector("#crypto0");
    aave.checked = true;
    ChangeProjectCrypto("aave");
  }, []);

  const changePriceHandler = (e) => {
    setPurchasePrice(e.target.value);
    setPurchasePriceType('number');
  };

  const changeAmountHandler = (e) => {
    setAmountPurchased(e.target.value);
    setAmountPurchasedType('number');
  };

  const changeSalePriceHandler = (e) => {
    setSalePrice(e.target.value);
    setSalePriceType('number');
  };

  useEffect(() => {
    setTotalInvested(parseFloat(amountPurchased * purchasePrice).toFixed(2));
    setFinalPosition(salePrice * amountPurchased);
    setProfitLoss(parseFloat((amountPurchased * salePrice) - totalInvested).toFixed(2));
    setProfitLossBorrow(parseFloat(finalPosition - repayInterestPlusLoan))
  }, [salePrice, amountPurchased, totalInvested]);


  useEffect(() => {
    setPurchasePrice('Set Purchase Price');
    setAmountPurchasedType('Set Amount');
    setSalePriceType('Set Sale Price');
  }, [])

  return (
    <>
      <h2 className={props.header}>Crypto Trade</h2>
      <h4 className={props.header}>Fill out Borrow section first</h4>
      <div className={props.container}>
        <div id={classes.id} className="select-options-container ">
          <div
            onClick={ChangeProjectCrypto}
            className="select-options-div-box"
            tabIndex="1"
          >
            {projectListCrypto}
            <img
              className="select-icon"
              src="http://cdn.onlinewebfonts.com/svg/img_295694.svg"
              alt="Arrow Icon"
              aria-hidden="true"
            />
          </div>
          <ul className="select-list">
            <div className="list-container">{projectListCryptoUl}</div>
          </ul>
        </div>{" "}
        <Input readonly title="Coin Price" value={price} />
        <Input
          readonly
          type="number"
          title="Borrowed Amount ADA"
          value={loanValueBorrow}
        />
        <Input
          readonly
          type="number"
          title="Borrowed Amount ($)"
          value={loanValueDollars}
        />
        <Input
          onChange={changePriceHandler}
          type={purchasePriceType}
          title="Purchase Price"
          value={purchasePrice}
        />
        <Input title='Max Amount' readonly name='max amount' type='number' value={maxAmount} />
        <Input
          title="Amount Purchased"
          name="amount"
          value={amountPurchased}
          onChange={changeAmountHandler}
          type={amountPurchasedType}
        />
        <Input readonly
          title="Total Invested"
          value={totalInvested}
          type="number"
        />
        <Input
          readonly
          type="number"
          title="Borrowed Payment ADA"
          value={loanWInterestBorrow}
        />
        <Input
          readonly
          type="number"
          title="Borrowed Payment ($)"
          value={repayInterestPlusLoan}
        />
        <Input
          title="Sale Price"
          type={salePriceType}
          value={salePrice}
          onChange={changeSalePriceHandler}
        />
        <Input readonly type='number' title='Final Position' value={finalPosition} />
        <Input readonly title="Profit/Loss Crypto Trade" value={profitLoss} type='number' />
        <Input readonly title='Profit/Loss Borrow Payment' value={profitLossBorrow} type='number' />
      </div>
    </>
  );
}

export default Crypto;
