import React, { useEffect, useState } from "react";
import Input from "../../Input/Input";
import classes from "./CNFT.module.css";
import axios from "axios";

const CNFT = (props) => {
  const { loanValueBorrow, loanWInterestBorrow } = { ...props.data };

  const [floor, setFloor] = useState();

  console.log(props.data);

  const projectList =[];
  const projectListUl = [];

  const ChangeProject = (e) => {
    let policyManual;
    let selectedPolicyCNFT;
    if (e.length > 1) {
      policyManual = e;
      selectedPolicyCNFT = null;
    } else {
      selectedPolicyCNFT = e.target.value;
      policyManual = null;
    }

    function sortPolicy() {
      if (policyManual !== null) {
        return policyManual;
      } else {
        return selectedPolicyCNFT;
      }
    }
    console.log(selectedPolicyCNFT);
    console.log(e);
    const config = { headers: { Accept: "application/json" } };

    const params = {
      policy: sortPolicy(),
    };
    axios
      .get(`https://api.opencnft.io/1/policy/${params.policy}`, config)
      .then((res) => {
        const volumeStr = `${(res.data.total_volume /= Math.pow(
          10,
          6
        )).toLocaleString()} ADA`;
        const floorNum = (res.data.floor_price /= Math.pow(10, 6));
        const floorStr = `${floorNum} ADA`;
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <>
      <h2 className={props.header}>CNFT Trade</h2>
      <div className={props.container}>
        <div className="select-options-container">
          <div
            onClick={ChangeProject}
            className="select-options-div-box"
            tabIndex="1"
          >
            {projectList}
            <img
              className="select-icon"
              src="http://cdn.onlinewebfonts.com/svg/img_295694.svg"
              alt="Arrow Icon"
              aria-hidden="true"
            />
          </div>
          <ul className="select-list">
            <div className="list-container">{projectListUl}</div>
          </ul>
        </div>
        <Input title="Floor Price" />
        <Input type="number" title="Borrowed Amount" value={loanValueBorrow} />
        <Input title="Purchase Price" />
        <Input
          type="number"
          title="Borrowed Payment"
          value={loanWInterestBorrow}
        />
        <Input title="Sale Price" />
        <Input title="Profit/Loss ADA" />
      </div>
    </>
  );
};

export default CNFT;
