import React, { useEffect, useState } from "react";
import Input from "../../Input/Input";
import classes from "./Crypto.module.css";
import {
  projectListCrypto,
  projectListCryptoUl,
} from "../../ReusableCryptoArray";
import axios from "axios";

function Crypto(props) {
  const {
    loanValueBorrow,
    loanWInterestBorrow,
    loanValueDollars,
    repayInterestPlusLoan,
  } = { ...props.data };

  const [price, setPrice] = useState();

  console.log(projectListCrypto);

  const ChangeProjectCrypto = (e) => {
    const selectedToken = e.target.value;
    const policyCall = e;


    const config = { headers: { Accept: "application/json" } };
    const params = {
      coinId: selectedToken,
    };
    axios
      .get(`https://api.coingecko.com/api/v3/coins/${params.coinId}`, config)
      .then((res) => {
        console.log(res);
        setTimeout(() => {
          setPrice(res.data.market_data.current_price.usd.toLocaleString());
        }, 500);

      })
      .catch((err) => {
        console.log(err);
      });
  };

  useEffect(() => {
    const aave = document.querySelector('#crypto0');
    aave.checked = true;

  })

  return (
    <>
      <h2 className={props.header}>Crypto Trade</h2>
      <div className={props.container}>
        <div id={classes.id} className="select-options-container ">
          <div
            onClick={ChangeProjectCrypto}
            className="select-options-div-box"
            tabIndex="1"
          >
            {projectListCrypto}
            <img
              className="select-icon"
              src="http://cdn.onlinewebfonts.com/svg/img_295694.svg"
              alt="Arrow Icon"
              aria-hidden="true"
            />
          </div>
          <ul className="select-list">
            <div className="list-container">{projectListCryptoUl}</div>
          </ul>
        </div>{" "}
        <Input title="Coin Price" value={price}/>
        <Input
          type="number"
          title="Borrowed Amount ADA"
          value={loanValueBorrow}
        />
        <Input
          type="number"
          title="Borrowed Amount ($)"
          value={loanValueDollars}
        />
        <Input title="Purchase Price" />
        <Input title="Amount Purchased" />
        <Input title="Total Invested" />
        <Input
          type="number"
          title="Borrowed Payment ADA"
          value={loanWInterestBorrow}
        />
        <Input
          type="number"
          title="Borrowed Payment ($)"
          value={repayInterestPlusLoan}
        />
        <Input title="Sale Price" />
        <Input title="Profit/Loss" />
      </div>
    </>
  );
}

export default Crypto;
