import React, { useEffect } from "react";
import Card from "../UI/Card";
import tokenSortList from "../CryptoProjectData";
import { useState } from "react";
import axios from "axios";
import DropdownUl from "../DropdownUl/DropdownUl";
import classes from "./CryptoData.module.css";
import Loading from "../UI/Loading";
import styles from "../Dropdown/Dropdown.module.css";
import Dropdown from "../Dropdown/Dropdown";
import AdjustProjectList from "../UI/AdjustProjectList";

function CryptoData(props) {




  const [check, setCheck] = useState(false);

  const ChangeCheckHandler = e => {
    e.target.checked = true;
    console.log(e.target)
  }



  const [loading, setLoading] = useState(true);

  const ChangeProjectCrypto = (e) => {
    let projectId;
    let selectedToken;
    if (e.length > 1) {
      projectId = e;
      selectedToken = null;
    } else {
      selectedToken = e.target.value;
      projectId = null;
    }

    function sortPolicy() {
      if (projectId !== null) {
        return projectId;
      } else {
        return selectedToken;
      }
    }
    const config = { headers: { Accept: "application/json" } };
    const params = {
      coinId: sortPolicy(),
    };
    axios
      .get(`https://api.coingecko.com/api/v3/coins/${params.coinId}`, config)
      .then((res) => {
        console.log(res);
        setTimeout(() => {
          setPrice(res.data.market_data.current_price.usd.toLocaleString());
          setGainLoss(
            res.data.market_data.price_change_percentage_24h_in_currency.usd
          );
          setMktCap(res.data.market_data.market_cap.usd.toLocaleString());
          setCryptoImg(res.data.image.small);
          setCoinName(res.data.name);
          console.log("setting");
        }, 300);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const [price, setPrice] = useState();
  const [gainLoss, setGainLoss] = useState();
  const [mktCap, setMktCap] = useState();
  const [cryptoImg, setCryptoImg] = useState();
  const [coinName, setCoinName] = useState();

  useEffect(() => {
    setTimeout(() => {
      setLoading(false);
    }, 600);
  }, [loading]);

  useEffect(() => {

    setTimeout(() => {
      const coin = document.querySelector("#crypto0");
      console.log(coin)
      projectListCrypto.map(x => x.props.textDropdown == 'AAVE' ? setCheck(true) : setCheck(false))
      console.log(projectListCrypto)
      ChangeProjectCrypto('aave')
    }, 450);
  }, []);

  return (
    <>
      {loading && <Loading></Loading>}
      {!loading && (
        <Card className={classes.container}>
          <AdjustProjectList
            imgClass={styles.img}
            containerClass={styles.selectOptionsContainer}
            onClick={ChangeProjectCrypto}
            boxClass={styles.selectOptionsDivBox}
            listOne={projectListCrypto}
            listTwo={projectListCryptoUl}
            iconClass={styles.selectIcon}
            selectListClass={styles.selectList}
            listContainerClass={styles.listContainer}
          />
          <table className={classes.cardanotable}>
            <tr className={classes.cardanotablerow}>
              <th>Symbol</th>
              <th>Coin</th>
              <th>Price</th>
              <th>24h %</th>
              <th>Mkt. Cap</th>
            </tr>
            <tr className={classes.cardanotablerow}>
              <td>
                <img className={classes.coinsymbol} src={cryptoImg}></img>
              </td>
              <td>{coinName}</td>
              <td>{price}</td>
              <td>{gainLoss}</td>
              <td>{mktCap}</td>
            </tr>
          </table>
          <div className={classes.thanks}>
            <h3>Thank you to Coingecko for data feed</h3>
          </div>
        </Card>
      )}
    </>
  );
}

export default CryptoData;
