import React from "react";
import { useState, useEffect } from "react";
import MobileNav from "./MobileNav";
import Nav from "./Nav";
import Button from "../UI/Button";
import classes from "./NavBar.module.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars } from "@fortawesome/free-solid-svg-icons";

const NavBar = () => {
  const [windowWidth, setWindowWidth] = useState(window.innerWidth);
  const [mobile, setMobile] = useState(false);
  const bars = <FontAwesomeIcon className={classes.icon} icon={faBars} />;

  const innerWidth = () => setWindowWidth(window.innerWidth);

  const MobileNavHandler = () => {
    console.log(mobile);

    if (mobile === false) {
      setMobile(true);
      console.log("true");
    } else {
      setMobile(false);
      console.log("false");
    }
  };

  useEffect(() => {
    window.addEventListener("resize", innerWidth);
    console.log(windowWidth);
    console.log(mobile);
  }, [windowWidth]);

  return(
    <div className={classes.container}>
        {windowWidth < 1000 ? <Button /> : windowWidth === true && windowWidth < 1000 ? console.log('hi') : console.log('wod')}
    </div>
  )
};

export default NavBar;
