import React, { useState } from "react";
import Card from "../UI/Card";
import HomeList from "./HomeList";
import classes from "./Home.module.css";
import add from "classnames";
import { Link } from "react-router-dom";
import Button from "../UI/Button";
import Plans from "../Plans/Plans";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import discord from "./discord.svg";
import twitter from "./twitter.svg";
import HomeButtonCard from "./HomeButtonCard";

const Home = () => {
  const appListArr = [
    "Borrow Assistance",
    "Lend Assistance",
    "Crypto Trade Assistance",
    "NFT Trade Assistance",
  ];
  const dataListArr = [
    "Floor Price, Volume, etc.",
    "Crypto Price, Market Cap, etc",
  ];
  const plansArr = [
    "Currently Free",
    "Monthly, Yearly, Life",
    "Follow socials for updates",
  ];
  const linksArr = [
    ["https://twitter.com/CNiphty", "CNiphty Twitter"],
    ["https://twitter.com/CNiphtyProphet", "CNiphtyProphet Twitter"],
    [
      "https://discord.com/channels/1028109414193975336/1028109415607455816",
      "Discord",
    ],
    [
      "https://medium.com/@programmedhart/cniphtypaper-1-0-dc8a72587ac4",
      "LitePaper",
    ],
  ];

  const detailsArr = [
    "This is V1, details on V2 will be announced throughout development and user input.",
    "APIs for data are free and restricted to a limit, if you are receiving errors that is due to the request limit being reached.",
    "Based on interest I will be able to gauge better API usage.",
    "This is my first project as a software developer, if you would like to hire me or collaborate feel free to dm me on twitter.",
  ];

  const app = appListArr.map((x) => (
    <li key={`app${x}`} className={classes.li}>
      {x}
    </li>
  ));
  const data = dataListArr.map((x) => (
    <li key={`data${x}`} className={classes.li}>
      {x}
    </li>
  ));
  const plans = plansArr.map((x) => (
    <li key={`plan${x}`} className={classes.li}>
      {x}
    </li>
  ));
  const links = linksArr.map((x) => (
    <li key={`link${x}`} className={classes.li}>
      <a className={classes.a} href={x[0]}>
        {x[1]}
      </a>
    </li>
  ));
  const details = detailsArr.map((x) => <li className={classes.li}>{x}</li>);

  const [flip, setFlip] = useState(false);

  const DetailsHandler = () => {
    console.log("hi");
    if (flip === false) {
      setFlip(true);
    }
  };
  console.log("hi");
  return (
    <>
      <h2 className={classes.header}>Borrow/Lending Assistance</h2>
      <Card className={add(classes.container)}>
        <Card className={classes.cardcontainer}>
          <HomeButtonCard
            path="/app"
            onClick={DetailsHandler}
            cardClass={classes.list}
            h2Class={classes.h2}
            titleStyle={classes.buttonPage}
            buttonPage={classes.buttonPage}
            detailsTitle="Borrow/Lending"
            detailsClass={classes.detailsButton}
          />
        </Card>
        <Card className={classes.cardcontainer}>
          <HomeButtonCard
            path="/cnftdata"
            onClick={DetailsHandler}
            cardClass={classes.list}
            h2Class={classes.h2}
            titleStyle={classes.buttonPage}
            buttonPage={classes.buttonPage}
            detailsTitle="Analyze Data"
            detailsClass={classes.detailsButton}
          />
        </Card>
        <Card className={classes.cardcontainer}>
          <HomeButtonCard
            path="/plans"
            onClick={DetailsHandler}
            cardClass={classes.list}
            h2Class={classes.h2}
            titleStyle={classes.buttonPage}
            buttonPage={classes.none}
            detailsTitle="Currently Free"
            detailsClass={classes.detailsButton}
          />
        </Card>
        <Card className={classes.cardcontainer}>
          <HomeButtonCard
          path='/'
            onClick={DetailsHandler}
            cardClass={classes.list}
            h2Class={classes.h2}
            detailsTitle="Official Links"
            detailsClass={classes.detailsButton}
          />
        </Card>
      </Card>
      <Card
        className={add(classes.detailscontainer, classes.list, classes.details)}
      >
        <HomeList children={details} header={"Extra Details"} />
      </Card>
    </>
  );
};

export default Home;
